mapper.select:
	select bi.*,br.regionName acceptUnitsWord,(
	SELECT br1.regionName FROM business_information bi1 LEFT JOIN business_region br1 ON bi1.threadAreaId = br1.id where bi1.id = #{id}
) threadArea from business_information bi LEFT JOIN business_region br ON bi.acceptUnits = br.id where bi.id = #{id}

mapper.update:
update business_information
threadAreaId = #{threadAreaId}, clueAddress = #{clueAddress}, industryField = #{industryField}, informType = #{informType}, informContent = #{informContent}, picture = #{picture}, video = #{video}, informerId = #{informerId}, validAssessorId = #{validAssessorId}, validFile = #{validFile}, investigationFile = #{investigationFile}, feedbackInformation = #{feedbackInformation}, state = #{state}, investigationAssessorId = #{investigationAssessorId}, endAssessorId = #{endAssessorId}, openId = #{openId}, alarmNumber = #{alarmNumber}, acceptUnits = #{acceptUnits}, acceptTime = #{acceptTime},
where id = #{id}

mapper.select:
select COUNT(*) from business_information 

where state = #{state} and (threadAreaId = #{threadAreaId} or acceptUnits = #{acceptUnits})
where threadAreaId = #{threadAreaId} or acceptUnits = #{acceptUnits}
